mapper.select:
SELECT * FROM room_info


        AND room_no LIKE CONCAT('%', #{roomNo}, '%')


        AND category_id = #{categoryId}


        AND price >= #{priceMin}


        AND price <= #{priceMax}


        AND room_status = #{roomStatus}


ORDER BY room_id DESC

