        AND category_id = #{categoryId}


        AND price >= #{priceMin}


        AND price <= #{priceMax}


        AND room_status = #{roomStatus}


ORDER BY room_id DESC
LIMIT #{offset}, #{pageSize}

SELECT COUNT(*) FROM room_info


        AND room_no LIKE CONCAT('%', #{roomNo}, '%')


        AND category_id = #{categoryId}


        AND price >= #{priceMin}


        AND price <= #{priceMax}


        AND room_status = #{roomStatus}

mapper.update:
UPDATE room_info
category_id = #{categoryId}, room_no = #{roomNo}, area = #{area}, bed_type = #{bedType}, price = #{price}, facilities = #{facilities}, cover_img = #{coverImg}, detail_imgs = #{detailImgs}, room_status = #{roomStatus}, update_time = #{updateTime}, remark = #{remark},
WHERE room_id = #{roomId}
