mapper.select:
SELECT * FROM room_category


        AND category_name LIKE CONCAT('%', #{categoryName}, '%')


        AND price_max >= #{priceMin}


        AND price_min <= #{priceMax}

    AND status = 1

ORDER BY sort ASC, category_id DESC

SELECT * FROM room_category


        AND category_name LIKE CONCAT('%', #{categoryName}, '%')


        AND price_max >= #{priceMin}


        AND price_min <= #{priceMax}

    AND status = 1

ORDER BY sort ASC, category_id DESC
LIMIT #{offset}, #{pageSize}

SELECT COUNT(*) FROM room_category


        AND category_name LIKE CONCAT('%', #{categoryName}, '%')


        AND price_max >= #{priceMin}


        AND price_min <= #{priceMax}

    AND status = 1

mapper.update:
UPDATE room_category
category_name = #{categoryName}, description = #{description}, price_min = #{priceMin}, price_max = #{priceMax}, cover_img = #{coverImg}, sort = #{sort}, status = #{status}, update_time = #{updateTime}, remark = #{remark},
WHERE category_id = #{categoryId}
