mapper.insert:
INSERT INTO user (
    user_name, password, real_name, phone, email, avatar,
    user_type, status, create_time, update_time, remark
) VALUES (
    #{userName}, #{password}, #{realName}, #{phone}, #{email}, #{avatar},
    #{userType}, #{status}, #{createTime}, #{updateTime}, #{remark}
)

mapper.update:
UPDATE user
user_name = #{userName}, password = #{password}, real_name = #{realName}, phone = #{phone}, email = #{email}, avatar = #{avatar}, user_type = #{userType}, status = #{status}, update_time = #{updateTime}, remark = #{remark},
WHERE user_id = #{userId}

mapper.select:
SELECT * FROM user


        AND user_name LIKE CONCAT('%', #{userName}, '%')


        AND real_name LIKE CONCAT('%', #{realName}, '%')


        AND phone LIKE CONCAT('%', #{phone}, '%')


        AND status = #{status}

    AND user_type = #{userType}

ORDER BY user_id DESC
LIMIT #{offset}, #{pageSize}

SELECT COUNT(*) FROM user


        AND user_name LIKE CONCAT('%', #{userName}, '%')


        AND real_name LIKE CONCAT('%', #{realName}, '%')


        AND phone LIKE CONCAT('%', #{phone}, '%')


        AND status = #{status}

    AND user_type = #{userType}
